import {Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import TrackVisibility from 'react-on-screen';
import work1 from '../imgs/test.jpg';

export default function Projects() {

  let projectsData = [
    {
      projectUrl: "https://google.com",
      projectBg: work1,
      techs: [
        {
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        },
        {
          icon: "fa-brands fa-sass",
          iconColor: "green"
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        },
      ]
    }
  ]

  return (
    <TrackVisibility offset={300}>
      {({ isVisible }) =>
        <section id="projects" className={isVisible ? "scrolled" : "scrolled"}>
          <Container>
            <h3 className='section-header'>اعمالي</h3>
            <Row className='align-items-center justify-content-center'>
              {
                projectsData.map((project, projectIndex) => {
                  return (
                    <Col key={projectIndex} md={12} lg={6} className="pb-5">
                      <div className='project'>
                        <a href={project.projectUrl} target="_blank" rel="noreferrer">
                          <div className='project-image' style={{backgroundImage: `url(${project.projectBg})`}}></div>
                          <div className='techs'>
                            {
                              project.techs.map((tech, techIndex) => {
                                return (
                                  <i key={techIndex} className={tech.icon} style={{color: tech.iconColor}}></i>
                                )
                              })
                            }
                          </div>
                        </a>
                      </div>
                    </Col>
                  )
                })
              }
            </Row>
          </Container>
        </section>
      }
    </TrackVisibility>
  )
}
