import {useState, useEffect} from 'react';
import {Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import rocket from '../imgs/rocket.svg'

export default function Banner() {

  let [text, setText] = useState("")
  let [isDeleting, setIsDeleting] = useState(false)
  let word = "مطور واجهات امامية"
  let duration = Math.floor(Math.random() * 1000)
  let updatedText

  useEffect(() => {
    function ticker() {
      if(isDeleting) {
        updatedText = word.substring(0, text.length - 1)
      }
      else {
        updatedText = word.substring(0, text.length + 1)
      }
      setText(updatedText)
    }
    setInterval(ticker, duration)
    return () => clearInterval(ticker, duration)
  })

  return (
    <section id="banner" className='d-flex justify-content-center align-items-center'>
      <Container>
        <Row className='align-items-center'>
          <Col lg={8} className='banner-text'>
            <span>مرحبا ، انا</span>
            <h1>باسل الشريف <span>{text}</span></h1>
            <p>مطور ويب وعندي شغف كبير لتعلم كل مايخص تطوير تطبيقات الويب</p>
            <div className='buttons'>
              <a href='/'>للتواصل</a>
              <a href='/'>تحميل السيرة الذاتية</a>
            </div>
          </Col>
          <Col lg={4}>
            <div className='rocket-container'>
              <img src={rocket} alt="rocket"></img>
              <div className='circles'>
                <span className='yellow'></span>
                <span className='red'></span>
                <span className='blue'></span>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  )
}
