import {Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import TrackVisibility from 'react-on-screen';
import speedTestBg from '../imgs/projects-bg/speed-test.png'
import memoryGameBg from '../imgs/projects-bg/memory-game.png'
import hotelBg from '../imgs/projects-bg/hotel.png'
import portfolioTemplateBg from '../imgs/projects-bg/portfolio-template.png'
import portfolioTemplate3Bg from '../imgs/projects-bg/portfolio-template3.png'
import portfolioTemplate2Bg from '../imgs/projects-bg/portfolio-template2.png'

export default function Projects() {

  let projectsData = [
    {
      projectUrl: "https://basel-01.github.io/typing-speed/",
      projectBg: speedTestBg,
      projectName: "speed test game",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-css3-alt",
          iconColor: "#264de4",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        },
      ]
    },
    {
      projectUrl: "https://basel-01.github.io/memory-game/",
      projectBg: memoryGameBg,
      projectName: "memory game",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-css3-alt",
          iconColor: "#264de4",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        },
      ]
    },
    {
      projectUrl: "https://basel-01.github.io/hotel/index.html",
      projectBg: hotelBg,
      projectName: "hotel template",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-css3-alt",
          iconColor: "#264de4",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        }
      ]
    },
    {
      projectUrl: "https://basel-01.github.io/portfolio-template/",
      projectBg: portfolioTemplateBg,
      projectName: "portfolio template",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-css3-alt",
          iconColor: "#264de4",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        },
        {
          icon: "fa-brands fa-bootstrap",
          iconColor: "#563d7c",
        }
      ]
    },
    {
      projectUrl: "https://basel-01.github.io/portfolio-template3/",
      projectBg: portfolioTemplate3Bg,
      projectName: "portfolio template",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-css3-alt",
          iconColor: "#264de4",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        }
      ]
    },
    {
      projectUrl: "https://basel-01.github.io/portfolio-template2/",
      projectBg: portfolioTemplate2Bg,
      projectName: "portfolio template",
      techs: [
        {
          icon: "fa-brands fa-html5",
          iconColor: "#e34c26",
        },
        {
          icon: "fa-brands fa-square-js",
          iconColor: "#f7df1e",
        },
        {
          icon: "fa-brands fa-sass",
          iconColor: "#cd6799",
        }
      ]
    }
  ]
  // 
  let skillsData = [
    {
      icon: "fa-brands fa-html5",
      iconColor: "#e34c26",
    },
    {
      icon: "fa-brands fa-css3-alt",
      iconColor: "#264de4",
    },
    {
      icon: "fa-brands fa-square-js",
      iconColor: "#f7df1e",
    },
    {
      icon: "fa-brands fa-react",
      iconColor: "#61dbfb",
    },
    {
      icon: "fa-brands fa-bootstrap",
      iconColor: "#563d7c",
    },
    {
      icon: "fa-brands fa-sass",
      iconColor: "#cd6799",
    }
  ]

  return (
    <TrackVisibility offset={300}>
      {({ isVisible }) =>
        <section id="projects" className={isVisible ? "scrolled" : "scrolled"}>
          <Container>
            <h3 className='section-header'>اعمالي</h3>
            <Row className='align-items-center justify-content-center'>
              {
                projectsData.map((project, projectIndex) => {
                  return (
                    <Col key={projectIndex} md={12} lg={6} className="pb-5">
                      <div className='project'>
                        <a href={project.projectUrl} target="_blank" rel="noreferrer">
                          <div className='project-image' style={{backgroundImage: `url(${project.projectBg})`}}></div>
                          <div className='project-desc'>
                            <p>{project.projectName}</p>
                            <div className='techs'>
                              {
                                project.techs.map((tech, techIndex) => {
                                  return (
                                    <i key={techIndex} className={tech.icon} style={{color: tech.iconColor}}></i>
                                  )
                                })
                              }
                            </div>
                          </div>
                        </a>
                      </div>
                    </Col>
                  )
                })
              }
            </Row>
          </Container>
        </section>
      }
    </TrackVisibility>
  )
}
