import {Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import TrackVisibility from 'react-on-screen';
import work1 from '../imgs/test.jpg';

export default function Works() {

  let worksData = [
    {
      workUrl: "https://google.com",
      workBg: work1,
      techs: [
        {
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        }
      ]
    },
    {
      workUrl: "https://google.com",
      workBg: work1,
      techs: [
        {
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        }
      ]
    },
    {
      workUrl: "https://google.com",
      workBg: work1,
      techs: [
        {
          techId: 1,
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        }
      ]
    },
    {
      workUrl: "https://google.com",
      workBg: work1,
      techs: [
        {
          icon: "fa-brands fa-square-js",
          iconColor: "red"
        }
      ]
    },
  ]

  return (
    <TrackVisibility offset={300}>
      {({ isVisible }) =>
        <section id="works" className={isVisible ? "scrolled" : ""}>
          <Container>
            <h3 className='section-header'>اعمالي</h3>
            <Row className='align-items-center justify-content-center'>
              {
                worksData.map(work => {
                  return (
                    <Col key={work.workId} md={6} className="pb-5">
                      <div className='work'>
                        <a href={work.workUrl} target="_blank" rel="noreferrer">
                          <div className='work-image' style={{backgroundImage: `url(${work.workBg})`}}></div>
                          <div className='techs'>
                            {
                              work.techs.map(tech => {
                                return (
                                  <i className={tech.icon} style={{color: tech.iconColor}}></i>
                                )
                              })
                            }
                          </div>
                        </a>
                      </div>
                    </Col>
                  )
                })
              }
            </Row>
          </Container>
        </section>
      }
    </TrackVisibility>
  )
}
