import {useState, useEffect} from 'react'
export default function ScrollToTop() {

  let [scrollToTopBtn, setScrollToTopBtn] = useState(false)

  useEffect(() => {
    window.addEventListener("scroll", () => {
      if(window.scrollY >= 400) {
        setScrollToTopBtn(true)
      }
      else {
        setScrollToTopBtn(false)
      }
    })
  }, [])

  function scrollUp() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    })
  }

  return (
    <button className={scrollToTopBtn ? "scroll-btn" : "scroll-btn show"} onClick={scrollUp}>
      <i className="fa-sharp fa-solid fa-jet-fighter-up"></i>
    </button>
  )
}
