import {useState, useEffect} from 'react'
export default function ScrollToTop() {

  let [scrollToTopBtn, setScrollToTopBtn] = useState(false)

  useEffect(() => {
    window.addEventListener("scroll", () => {
      if(window.scrollY >= 400) {
        setScrollToTopBtn(true)
      }
      else {
        setScrollToTopBtn(false)
      }
    })
  }, [])

  function scrollUp() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    })
  }

  let st = {
    backgroundColor: "red",
    position: "fixed",
    bottom: "50px",
    right: "50px",
    width: "50px",
    height: "50px",
  }

  return (
    <>
      {
        scrollToTopBtn &&
        <button style={st}>
          <i className="fa-sharp fa-solid fa-jet-fighter-up"></i>
        </button>
      }
    </>
  )
}
